import React from "react";
import PropTypes from "prop-types";
import { Button } from '../atoms/Button';
import "./accordion.css";

/**
 * Primary UI component for user interaction
 */
export const Accordion = ({ activeState, accordionHead, panelData, ...props }) => {
  return (
    <div>
        <Button isActive={activeState} accordionClassName="accordion" label={accordionHead}/>
      <div className="panel">
        <p>
          {panelData}
        </p>
      </div>
    </div>
  );
};

Accordion.propTypes = {};

Accordion.defaultProps = {
    activeState: false,
    accordionHead: 'Section 1',
    panelData: 'Test'
};
